sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    var oldValueLabel = "";
    var newValueLabel = "";


    switch (oldValue) {
        case "hr":
            oldValueLabel = "Human Resources";
            break;
        case "facilities":
            oldValueLabel = "Facilities";
            break;
        case "legal":
            oldValueLabel = "Legal";
            break;
        default:
            oldValueLabel = oldValue; // Keep the old value as is if it doesn't match any case
            break;
    }


    switch (newValue) {
        case "hr":
            newValueLabel = "Human Resources";
            break;
        case "facilities":
            newValueLabel = "Facilities";
            break;
        case "legal":
            newValueLabel = "Legal";
            break;
        default:
            newValueLabel = newValue; // Keep the new value as is if it doesn't match any case
            break;
    }

    g_form.addInfoMessage("Request Type changed from " + oldValueLabel + " to " + newValueLabel);
}
